import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './ClassPage.css';
import WebSocketService from './WebSocketService';

const ClassPage = ({ match }) => {
  const [classDetails, setClassDetails] = useState(null);
  const [studentDetails, setStudentDetails] = useState(null);
  const [notifications, setNotifications] = useState([]);
  const webSocketRef = useRef(null);
  const intervalRef = useRef(null);

  const studentId = match.params.studentId;
  const classId = match.params.classId;

  function onMessage(notification) {
    console.log('Received notification:', notification);
    setNotifications((prevNotifications) => [...prevNotifications, notification]);
  }

  useEffect(() => {
    console.log('Connecting to WebSocket...');
    webSocketRef.current = WebSocketService(onMessage);
    webSocketRef.current.connect();

    // Subscribe to the class (replace 'ClassName' with the actual class name)
    const subscription = webSocketRef.current.subscribeToClass('ClassName');

    return () => {
      if (subscription) {
        subscription.unsubscribe();
      }
      console.log('Disconnecting from WebSocket...');
      webSocketRef.current.disconnect();
    };
  }, []);

  const fetchData = async () => {
    try {
      if (studentId) {
        const response1 = await fetch(`/students/${studentId}`);
        const data1 = await response1.json();
        setStudentDetails(data1);
      }

      const response = await fetch(`/classes/${classId}`);
      const data = await response.json();
      setClassDetails(data);
    } catch (error) {
      console.error('Error fetching class details:', error);
    }
  };

  const startDataFetchInterval = () => {
    intervalRef.current = setInterval(fetchData, 30000);
  };

  const stopDataFetchInterval = () => {
    clearInterval(intervalRef.current);
  };

  useEffect(() => {
    fetchData(); // Initial data fetch
    startDataFetchInterval();

    return () => {
      stopDataFetchInterval();
    };
  }, [classId, studentId]);

  if (!classDetails) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      {/* Component content */}
    </div>
  );
};

export default ClassPage;
