// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';

const ClassDetails = ({ match }) => {
  // Extract classId from the URL params
  const classId = match.params.classId;

  // Fetch class details based on classId (similar to the useEffect in App component)
  // ...

  return (
    <div>
      {/* Render class details here */}
      <h2>Class Details for {classId}</h2>
      {/* ... */}
    </div>
  );
};

const ClassCard = ({ sClass }) => (
  <div className="class-container">
    <Link to={`/class/${sClass.id}`}>
      <h3>{sClass.classname}</h3>
    </Link>
    {
      // Render other class details here
      // ...

    }
  </div>
);

const App = () => {
  const [classes, setClasses] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/classes');
        const data = await response.json();
        setClasses(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <Router>
      <div className="App">
        <header className="App-header">

          <div className="App-intro">
            <h2>Classes</h2>
            <div className="class-container-wrapper">
              {classes.map((sClass) => (
                <ClassCard key={sClass.id} sClass={sClass} />
              ))}
            </div>
          </div>
        </header>
        <Route path="/class/:classId" component={ClassDetails} />
      </div>
    </Router>
  );
};

export default App;
