// WebSocketService.js

import { Client } from '@stomp/stompjs';

const WebSocketService = (onMessageCallback) => {
  const client = new Client({
    brokerURL: 'http://localhost:8080/ws', // Your WebSocket endpoint
    debug: function (str) {
      console.log(str);
    },
    reconnectDelay: 5000,
    heartbeatIncoming: 4000,
    heartbeatOutgoing: 4000,
  });

  const connect = () => {
    client.activate();
  };

  const disconnect = () => {
    client.deactivate();
  };

  const subscribeToClass = (className) => {
    const subscription = client.subscribe(`/classes/${className}`, (message) => {
      onMessageCallback(JSON.parse(message.body));
    });

    return subscription;
  };

  return { connect, disconnect, subscribeToClass };
};

export default WebSocketService;
